CREATE SEQUENCE DECO_ID_DECO_SEQ 
    MINVALUE 1 
    MAXVALUE 999999999 
    NOCACHE 
    ORDER ;

CREATE OR REPLACE TRIGGER DECO_ID_DECO_TRG 
BEFORE INSERT ON DESCUENTO_COMERCIAL 
FOR EACH ROW 
WHEN (NEW.ID_DECO IS NULL) 
BEGIN 
    SELECT DECO_ID_DECO_SEQ.NEXTVAL INTO :NEW.ID_DECO FROM DUAL; 
END;
/

